    public static int hasFile(String filepath) {
        
        return InternalFiles.filenames.indexOf(filepath);
    }

    public static boolean add(String filepath, String data) {

        if(hasFile(filepath) != -1) return false;

        InternalFiles.filenames.add(filepath);
        InternalFiles.data.add(data);

        return true;
    }

    public static String get(String filepath) {

        int index = hasFile(filepath);

        if(index == -1) return null;

        return data.get(index);
    }
